import React, { useState, useEffect} from "react";
import { connect } from "react-redux"
import { addTodo, getTodos, clearErrors } from "../../actions/TodoActions"

export const AddTodoItem = ({errors, addTodo, getTodos, clearErrors}) => {
  //state
  const [title, setTitle] = useState('');
  const [deadline, setDeadline] = useState('');
  const [todolist, setTodolist] = useState('fake');

  useEffect(() => {
    clearErrors()

  }, [])


  const onTitleChange = (e) => {
    setTitle(e.target.value);
    clearErrors()
  };
  const onDeadlineChange = (e) => {
    setDeadline(e.target.value);
    clearErrors()
  };

  //submit form and persist to localstorage
  const onSubmit = (e) => {
    e.preventDefault();
    const data = {
      todolist,
      title,
      deadline
    }
    addTodo(data)
    getTodos()
  };

  return (
    <div id="add_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Add Todo</h6>
          <div className="row">
            <div className="input-field col s12">
              <input
                type="text"
                name="title"
                value={title}
                onChange={onTitleChange}
              />
              <label htmlFor="title">Title</label>
            </div>
            <div className="input-field col s12">
              <input
                type="date"
                name="deadline"
                value={deadline}
                onChange={onDeadlineChange}
              />
              <label htmlFor="deadline">Deadline</label>
              <span className="helper-text" data-error="wrong" data-success="right">Set the deadline for the todo. default is infinity</span>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="waves-effect waves-light btn">
            <i className="material-icons left">save</i>ADD
          </button>
        </div>
      </form>
      {
        errors !== null && <div className="text-center mt-4 mb-2 italic">
          there is an error
      </div>
      }

    </div>
  );
};


const mapStateToProps = state => ({
  errors : state.todo.error
})

export default connect(mapStateToProps, { addTodo , getTodos, clearErrors})(AddTodoItem);
