import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { addTodo, getTodos, clearErrors } from "../../actions/TodoActions";

export const AddTodoItem = ({ errors, addTodo, getTodos, clearErrors }) => {
  //state
  const [title, setTitle] = useState("");
  const [deadline, setDeadline] = useState("");
  const [todolist, setTodolist] = useState("fake");

  useEffect(() => {
    clearErrors();
  }, []);

  const onTitleChange = (e) => {
    setTitle(e.target.value);
    clearErrors();
  };
  const onDeadlineChange = (e) => {
    setDeadline(e.target.value);
    clearErrors();
  };

  //submit form and persist to localstorage
  const onSubmit = (e) => {
    e.preventDefault();
    const data = {
      todolist,
      title,
      deadline,
    };
    addTodo(data);
    getTodos();
  };

  return (
    <div id="add_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Add Todo</h6>
          <div className="row">
            <div className="input-field col s12">
              <input
                type="text"
                name="title"
                value={title}
                onChange={onTitleChange}
              />
              <label htmlFor="title">Title</label>
              {errors?.title && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.title[0]}
                </span>
              )}
            </div>
            <div className="input-field col s12">
              <input
                type="date"
                name="deadline"
                value={deadline}
                onChange={onDeadlineChange}
              />
              <label htmlFor="deadline">Deadline</label>
              {errors?.deadline && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.deadline[0]}
                </span>
              )}
            </div>
            <div class="input-field col s12">
              <select>
                <option value="" disabled selected>
                  Choose your option
                </option>
                <option value="1">Option 1</option>
                <option value="2">Option 2</option>
                <option value="3">Option 3</option>
              </select>
              <label>Select associated todolist</label>
              {errors?.todolist && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.todolist[0]}
                </span>
              )}
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button
            type="submit"
            className="waves-effect waves-light btn"
            disabled={errors !== null}
          >
            <i className="material-icons left">save</i>ADD
          </button>
        </div>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => ({
  errors: state.todo.errors,
});

export default connect(mapStateToProps, { addTodo, getTodos, clearErrors })(
  AddTodoItem
);
