  getTodos()
  var stodos = [];
  if (localStorage.getItem("todos")) {
      stodos = JSON.parse(localStorage.getItem("todos"));
  }
  stodos.push({
      id: Math.abs(new Date()),
      description: data.description,
      deadline: data.deadline,
      status: false,
  });
  await localStorage.setItem("todos", JSON.stringify(stodos));
  getTodos()
