import {REGISTER_USER, LOGIN_USER, USER_ERROR} from "../actions/types"


//initialise state
const initialState = {
    user: null,
    login: false,
    errors: null,
    success: null
}

export default (state = initialState, action) => {
    switch (action.type) {
        case REGISTER_USER:
        case LOGIN_USER:
            return{
                ...state,
                user: action.payload.data,
                login: true,
                success: action.payload.message
            }
        case USER_ERROR:
            return{
                ...state,
                errors: action.payload
            }

        default:
            return state
    }
}
