import React, { useState, useEffect } from "react";
import {connect } from "react-redux"
import { current, getTodos, updateTodo } from "../../actions/TodoActions"


const EditTodoList = ({current, updateTodo, getTodos}) => {

    //state
    const [title, setTitle] = useState('')
    const [deadline, setDeadline] = useState('')

    useEffect(() => {
      if (current) {
        setTitle(current.title)
        setDeadline(current.deadline)
      }
    }, [current])



    //ON INPUT CHANGES
    const onChange = (e) =>{
        setTitle(e.target.value)
    }
    const onDateChange = (e) =>{
        setDeadline(e.target.value)
    }

    //submit the form
    const onSubmit = (e) =>{
        e.preventDefault()
        const data = {
          title, deadline
        }
        updateTodo(current.id, data)
        getTodos()
    }


  return (
    <div id="edit_todo_modal" className="modal">
     {/*  <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Edit New Todo List</h6>
          <div className="row">
            <div className="input-field col s12">
              <input type="text" name="title" value={title} onChange={onChange} />

            </div>
          </div>
          <div className="row">
            <div className="input-field col s12">
              <input type="date" name="deadline" value={deadline} onChange={onDateChange} />

            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="modal-close waves-effect waves-light btn">
            <i className="material-icons left">update</i>UPDATE
          </button>
        </div>
      </form> */}
    </div>
  );
};


const mapStateToProps = state => ({
  current: state.todo.current,
})

export default connect(mapStateToProps, {updateTodo, getTodos})(EditTodoList);
