import React, { useState } from "react";
import { Link } from "react-router-dom";

const Register = () => {
  //iniatialise state
  const [errorMessage, setErrorMessage] = useState("Error message");
  const [data, setData] = useState({
    username: "",
    password: "",
  });
  //destructure variables in data object
  const { username, password } = data;
  //when input changes
  const onChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };
  //when the form is submitted
  const onSubmit = (e) => {
    e.preventDefault();
    if (username === "" || password === "") {
      setErrorMessage("All fields are required");
    } else {
      /* addOwner(data); */
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        height: "calc(100vh - 100px)",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <h4
        className="mb-4 text-center s12"
        style={{
          color: "#34962e",
          fontFamily: "monospace,serif,times",
          fontSize: "22px",
        }}
      >
        Create an Account
      </h4>
      <br />
      <br />
      <form
        onSubmit={onSubmit}
        className="container"
        style={{ maxWidth: "450px" }}
      >
        <div className="row">
          <form className="col s12">
            <div className="row">
              <div className="input-field col s12">
                <i className="material-icons prefix">account_circle</i>
                <input
                  id="icon_prefix"
                  type="text"
                  name="username"
                  value={username}
                  onChange={onChange}
                  className="validate"
                />
                <label htmlFor="icon_prefix">Username</label>
                <span
                  className="helper-text"
                  data-error="wrong"
                  data-success="right"
                >
                  Helper text
                </span>
              </div>
              <div className="input-field col s12">
                <i className="material-icons prefix">lock</i>
                <input
                  id="password"
                  type="password"
                  name={password}
                  value={password}
                  onChange={onChange}
                  className="validate"
                />
                <label htmlFor="password">Password</label>
                <span
                  className="helper-text"
                  data-error="wrong"
                  data-success="right"
                >
                  Helper text
                </span>
              </div>
            </div>
            {errorMessage && (
              <div
                className="helper-text center italic"
                data-error="wrong"
                data-danger="right"
                style={{ color: "red" }}
              >
                Error Messages go here
              </div>
            )}

            <div className="w-full text-right mt-4">
              <button type="submit" className="waves-effect waves-light btn" s>
                <i className="material-icons left">save</i>Register
              </button>
            </div>
          </form>
          <br />
          <p className="text-center">
            Already hav an account?, Please <Link to="/login">login</Link>
          </p>
        </div>
      </form>
    </div>
  );
};

export default Register;
