import React, { useEffect} from 'react'
import { Link } from "react-router-dom";
import { connect } from "react-redux"
import { getTodolists } from "../../actions/TodolistActions"


const HomePage = ({mytodolists:{todolists, loading}, getTodolists}) => {
    useEffect(() =>{
        getTodolists()
    },[])
    return (
        <div className="homepage">

            <p className="text-grey text-center"> 
                In order to be able to keep track of your todos, You need to
                <Link to="/register" className="link-hover"> Create an account</Link> with us. <br />
                You already have an <Link to="/register" className="link-hover">account</Link> You can please <Link to="/login">login</Link>
            </p>
            <p className="text-grey"> 
                You have the following list of TodoList:
            </p>
            <ul className="homepagelists">
                {
                    !loading ? (
                        todolists?.length > 0 ? (
                            todolists.map(t =>{
                               return <li key={t.id}>
                                   <i className="material-icons center" style={{lineHeight:'0', fontSize:'inherit', color:'purple'}}>event_note</i> 
                                   <Link to={`${t.id}/todos`} className="collection-item">
                                       <span className="badge">{t.todos?.length}</span>{t.name}
                                    </Link>
                                </li> 
                            })
                        ):('')
                    ):("loading")
                }
            </ul>
            <p className="text-grey">
                You have not yet create a todolist

            </p>
            <button className="waves-effect modal-trigger waves-light btn-small" href="#create_todolist_modal"><i className="material-icons left">create_new_folder</i>Create New</button>

        </div>
    )
}


const mapStateToProps = state =>({
    mytodolists: state.todolist,
})

export default connect(mapStateToProps,{getTodolists}) (HomePage);
