import axios from "axios";

import {
  ADD_TODO,
  DELETE_TODO,
  ERRORS,
  UPDATE_TODO,
  GET_TODOS,
  GET_TODO,
  SET_LOADING,
  CLEAR_ERRORS,
  CLEAR_CURRENT,
} from "./types";

const config = {
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  },
};
//actions

//add todo item to server
export const addTodo = (item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/additem`, item, config)
    .then((res) => {
      dispatch({
        type: ADD_TODO,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//get todos from server
export const getTodos =
  (id = 1) =>
  async (dispatch) => {
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/todos/${id}`);
      const data = await res.json();
      dispatch({
        type: GET_TODOS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: ERRORS,
        payload: "Something went wrong",
      });
    }
  };

//delete todo from locastorage
export const deleteTodo = (id) => async (dispatch) => {
  await axios
    .delete(`${process.env.REACT_APP_API_URL}/todo/${id}`, config)
    .then((res) => {
      /* dispatch({
        type: DELETE_TODO,
        payload: res.data,
      }); */
      alert(`${res.data}`)
      getTodos()
    })
    .catch((err) => {
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//update a todo item
export const updateTodo = (id, item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/updateitem/${id}`, item, config)
    .then((res) => {
      dispatch({
        type: UPDATE_TODO,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//set the current
export const setCurrent = (data) => (dispatch) => {
  dispatch({
    type: GET_TODO,
    payload: data,
  });
};

//clear todo errors
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS,
  };
};

//clear current
export const clearCurrent = () => {
  return {
    type: CLEAR_CURRENT,
  };
};

//set loading
export const setLoading = () => {
  console.log("loadingS");
  return {
    type: SET_LOADING,
  };
};
