import axios from "axios";
import M from "materialize-css/dist/js/materialize.min.js";


import {
  ADD_TODO_LIST,
  DELETE_TODO_LIST,
  TODO_LIST_ERRORS,
  UPDATE_TODO_LIST,
  GET_TODO_LIST,
  GET_TODO_LISTS,
  SET_TODOLIST_LOADING,
  CLEAR_TODOLIST_ERRORS,
  CLEAR_TODOLIST_CURRENT,
} from "./types";

const config = {
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  },
};
//messages for toast
const error_message = (message) => {
  return `<span class="error_messge_icon small"></span> <span>${message}</span>`;
};
const success_message = (message) => {
  return `<span class="success_messge_icon small"></span> <span>${message}</span>`;
};



//actions

//add todo item to server
export const addTodolist = (item) => async (dispatch) => {
  /* setLoading() */
  await axios
    .post(`${process.env.REACT_APP_API_URL}/addtodolist`, item, config)
    .then((res) => {
      M.toast({ html: success_message(res.data.message) });
      dispatch({
        type: ADD_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_LIST_ERRORS,
        payload: err.response && err.response.data.errors,
      });
    });
};

//get todos from server
export const getTodolists = (id) => async (dispatch) => {
  setLoading();
  await axios
    .get(`${process.env.REACT_APP_API_URL}/todolists/${id}`, config)
    .then((res) => {
      dispatch({
        type: GET_TODO_LISTS,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_LIST_ERRORS,
        payload: "Something went wrong",
      });
    });
};

//delete todo from locastorage
export const deleteTodolist = (id) => async (dispatch) => {
  await axios
    .delete(`${process.env.REACT_APP_API_URL}/todolist/${id}`, config)
    .then((res) => {
      dispatch({
        type: DELETE_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_LIST_ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//update a todo item
export const updateTodolist = (id, item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/updatetodolist/${id}`, item, config)
    .then((res) => {
      dispatch({
        type: UPDATE_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_LIST_ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//set the current
export const setCurrent = (data) => (dispatch) => {
  dispatch({
    type: GET_TODO_LIST,
    payload: data,
  });
};

//clear todo errors
export const clearTodolistErrors = () => {
  return {
    type: CLEAR_TODOLIST_ERRORS
  };
};

//clear current
export const clearCurrent = () => {
  return {
    type: CLEAR_TODOLIST_CURRENT,
  };
};

//set loading
export const setLoading = () => (dispatch) => {
  dispatch ({
    type: SET_TODOLIST_LOADING,
  });
};
