import React, { useState } from "react";
import { connect } from "react-redux"
import { addTodolist, getTodolists } from "../../actions/TodolistActions";

const CreateTodoList = ({ getTodolists, addTodolist }) => {

    //state
    const [todolistName, setTodolistName] = useState('')


    //ON INPUT CHANGES
    const onChange = (e) =>{
        setTodolistName(e.target.value)
    }

    //submit the form
    const onSubmit = (e) =>{
        e.preventDefault()
        const data = {
          todolistName : todolistName,
          ownerId : 1
        }
        addTodolist(data)
        getTodolists()
    }


  return (
    <div id="create_todolist_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Create New Todo List</h6>
          <div className="row">
            <div className="input-field col s12">
              <input type="text" name="todolistName" value={todolistName} onChange={onChange} />
              <label htmlFor="name">Name</label>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="waves-effect waves-light btn">
            <i className="material-icons left">save</i>Create
          </button>
        </div>
      </form>
    </div>
  );
};

