import React, { useEffect } from "react";
import { connect } from "react-redux";
import { useHistory, useParams } from "react-router-dom";
import DataTable from "react-data-table-component";
import AddTodoItem from "../modals/AddTodoItem";
import { addTodo, clearErrors, deleteTodo, setCurrent, updateStatus} from "../../actions/TodoActions";
import { getTodolist, getTodolists } from "../../actions/TodolistActions";
import EditTodoItem from "../modals/EditTodoItem";
import TodoItemDetails from "../modals/TodoItemDetails";
import Loader from "../inc/Loader";
import TodolistSummary from "./partials/TodolistSummary";

const ListTodos = ({
  mytodolists: { current_todolist }, owner: {user},
  setCurrent,
  updateStatus,
  getTodolist,
  loading,
  getTodolists,
}) => {
  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    id ? getTodolist(id) : history.push("/");
    getTodolists(user.id)
    // eslint-disable-next-line
  }, [id]);


  useEffect(() => {
    if (current_todolist &&  Object.keys(current_todolist).length === 0 && current_todolist.constructor === Object) {
      history.push("/");
    }
    // eslint-disable-next-line
  }, [current_todolist]);

  const changeStatus = async (t_id, status) => {
    await updateStatus(t_id, status);
    await getTodolist(id);
  };

  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "4px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "#5b968e",
        minHeight: "42px",
        fontSize: "1.2rem",
      },
    },
  };
  //columns of the table
  const columns = [
    {
      name: "Title",
      selector: (row) => row["title"],
      cell: (row) => (
        <a
          href="#todo_details_modal"
          className="modal-trigger"
          onClick={() => setCurrent(row)}
        >
          {row["title"]}
        </a>
      ),
      sortable: true,
      grow: 4,
    },
    {
      name: "Status",
      selector: (row) => row["completed"],
      cell: (row) =>
        row["completed"] === 1 ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
      center: true,
    },
    {
      name: "Switch",
      selector: (row) => row["completed"],
      cell: (row) => (
        <div className="switch">
          <label>
            <input
              type="checkbox"
              checked={!row.completed}
              onChange={() => changeStatus(row.id, row.completed)}
              value={row.completed}
            />
            <span className="lever"></span>
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <a
            href="#edit_todo_modal"
            className="modal-trigger"
            onClick={() => setCurrent(row)}
          >
            <i className="material-icons center cursor-pointer mx-4">edit</i>
          </a>
          <a
            href="#confirmDelete"
            className="modal-trigger"
            onClick={() => setCurrent(row)}
          >
            <i className="material-icons center cursor-pointer deleteCan mx-4">
              delete_forever
            </i>
          </a>
        </div>
      ),
      right: true,
    },
  ];

  return (
    <div style={{ marginBottom: "5%" }}>
      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger add-todo-floating-button"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="todos-header text-center w-full">
        {current_todolist && current_todolist.name}
      </div>
      <AddTodoItem />
      <EditTodoItem />
      <TodoItemDetails />
      <div className="block">
        {!loading ? (
          current_todolist?.todos?.length > 0 ? (
            <DataTable
              title="Todos"
              columns={columns}
              data={current_todolist.todos}
              customStyles={customStyles}
              pagination={true}
            />
          ) : (
            <div className="text-center" style={{ marginTop: "25%" }}>
              No Todos in this Todolist Folder Yet
            </div>
          )
        ) : (
          <Loader />
        )}
      </div>
      <TodolistSummary />
    </div>
  );
};

const mapStateToProps = (state) => ({
  todo: state.todo,
  mytodolists: state.todolist,
  owner: state.user,
});

export default connect(mapStateToProps, {
  setCurrent,
  deleteTodo,
  addTodo,
  updateStatus,
  clearErrors,
  getTodolist,
  getTodolists,
})(ListTodos);
