import axios from "axios";
import M from "materialize-css/dist/js/materialize.min.js";

import {REGISTER_USER, LOGIN_USER, USER_ERROR, SET_LOGIN} from "./types"

//configuration
const config ={
    headers:{
        'Content-type' :'application/json'
    }
}

//check if there is a user object in localstorage 
//if there is, turn login in state to true
/* if (localStorage.getItem("todo_user")) {
    $: loggedin()
  }

const loggedin = () =>{
    return {
        type: SET_LOGIN,
        payload: JSON.parse(localStorage.getItem("todo_user"))
    }
}
 */


//actions
//CREATE AN ACCOUNT
export const register = (data) => async (dispatch) =>{
    await axios.post(`${process.env.REACT_APP_API_URL}/register/user`, data, config)
    .then(res =>{
        dispatch({
            type: REGISTER_USER,
            payload: res.data
        })
        //update local storage with res.data
        localStorage.setItem("todo_user", JSON.stringify(res.data.data))
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}


//login user
export const login = (data) =>async (dispatch) =>{
    await axios.get(`${process.env.REACT_APP_API_URL}/login/user`, data, config)
    .then(res =>{
        dispatch({
            type: LOGIN_USER,
            payload: res.data
        })
        //update local storage with res.data
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}
