import React, { useState, useEffect } from "react";
import {connect } from "react-redux"
import { clearErrors,  updateTodo } from "../../actions/TodoActions"
import { getTodolist } from "../../actions/TodolistActions";


const EditTodoList = ({current,errors, mytodolists, updateTodo,  clearErrors, getTodolist}) => {

    //state
    const [data, setData] = useState({
      title : '',
      deadline: '',
      todolist:'',
    })
    const { title, deadline, todolist} = data;

    useEffect(() => {
      if (current) {
        setData({
          title: current.title,
          deadline: current.deadline,
          todolist: current.todolist_id,
        })
      }
    }, [current])



    //ON INPUT CHANGES
    const onChange = (e) =>{
        setData({...data, [e.target.name] : e.target.value})
        if(errors){
          clearErrors()
