import React, { useState } from "react";

const CreateTodoList = () => {

    //state
    const [todolistName, setTodolistName] = useState('HELLO')


    //ON INPUT CHANGES
    const onChange = (e) =>{
        setTodolistName(e.target.value)
    }

    //submit the form
    const onSubmit = () =>{
        alert('hello world')
    }


  return (
    <div id="create_todolist_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Create New Todo List</h6>
          <div className="row">
            <div className="input-field col s12">
              <input type="text" name="todolistName" value={todolistName} onChange={onChange} />
              <label htmlFor="name">Name</label>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <a className="waves-effect waves-light btn">
            <i className="material-icons left">save</i>Create
          </a>
        </div>
      </form>
    </div>
  );
};

export default CreateTodoList;
