import {REGISTER_USER, LOGIN_USER, USER_ERROR,SET_LOGIN, CLEAR_USER_ERRORS} from "../actions/types"


//initialise state
const initialState = {
    user: null,
    login: false,
    errors: null,
    success: null
}

if (localStorage.getItem("todo_user")) {
    initialState.user = JSON.parse(localStorage.getItem("todo_user"))
    initialState.login = true
  }

export default (state = initialState, action) => {
    switch (action.type) {
        case REGISTER_USER:
        case LOGIN_USER:
            return{
                ...state,
                user: action.payload.data,
                login: true,
                success: action.payload.message,
                errors: null
            }
        case SET_LOGIN:
            return{
                ...state,
                user: action.payload,
                login: true,
                errors:null
            }


        case USER_ERROR:
            return{
                ...state,
                errors: action.payload,
                login: false,
                success: null
            }


        case CLEAR_USER_ERRORS:
            return{
                ...state,
                errors: null,
                success: null
            }

        default:
            return state
    }
}
