import {ADD_TODO,DELETE_TODO,ERRORS,UPDATE_TODO,GET_TODOS,GET_TODO,SET_LOADING, CLEAR_ERRORS} from './types'


//actions

//add todo item to server
export const addTodo = (data) => async dispatch=>{
    dispatch({
        type:ADD_TODO,
        payload: data
    })
}

//get todos from server
export const getTodos = () => async dispatch =>{
    try {
        fetch('https://jsonplaceholder.typicode.com/todos')
  .then((response) => response.json())
  .then((json) => dispatch({
        type: GET_TODOS,
        payload: json}));

    } catch (error) {
        dispatch({
            type: ERRORS,
            payload: error.response
        })
    }
}


