import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { addTodo, getTodos, clearErrors } from "../../actions/TodoActions";
import { getTodolists } from "../../actions/TodolistActions";

export const AddTodoItem = ({ todolists, errors, addTodo, getTodos, clearErrors }) => {
  //state
  const [data, setData] = useState({title:'', deadline:'', todolist:''})

  const { title, deadline, todolist } = data;


  useEffect(() => {
    clearErrors();
  }, []);

  const onChange = (e) => {
    setData({...data, [e.target.name]: e.target.value})
    clearErrors();
  };

  //submit form and persist to localstorage
  const onSubmit = (e) => {
    e.preventDefault();
    addTodo(data);
    getTodos();
  };

  return (
    <div id="add_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Add Todo</h6>
          <div className="row">
            <div className="input-field col s12">
              <input
                type="text"
                name="title"
                value={title}
                onChange={onChange}
              />
              <label htmlFor="title">Title</label>
              {errors?.title && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.title[0]}
                </span>
              )}
            </div>
            <div className="input-field col s12">
              <input
                type="date"
                name="deadline"
                value={deadline}
                onChange={onChange}
              />
              <label htmlFor="deadline">Deadline</label>
              {errors?.deadline && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.deadline[0]}
                </span>
              )}
            </div>
            <div className="input-field col s12">
              <select name="todolist" value={todolist} onChange={onChange}>
                {
                  todolists?.length > 0 ? (
                    todolists.map(t =>{
                      return <option value={t.id} key={t.id}>{t.name}</option>
                    })
                  ):(
                    {/* <option value="none" disabled>No todolist </option> */}
                  )
                }
              </select>
              <label>Select associated todolist</label>
              {errors?.todolist && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.todolist[0]}
                </span>
              )}
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button
            type="submit"
            className="waves-effect waves-light btn"
            disabled={errors !== null}
          >
            <i className="material-icons left">save</i>ADD
          </button>
        </div>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => ({
  errors: state.todo.errors,
  todolists: state.todolist.todolists
});

export default connect(mapStateToProps, { addTodo, getTodos, clearErrors })(
  AddTodoItem
);
