import axios from "axios";
import M from "materialize-css/dist/js/materialize.min.js";


import {
  ADD_TODO,
  DELETE_TODO,
  TODO_ERRORS,
  UPDATE_TODO,
  GET_TODOS,
  GET_TODO,
  SET_TODO_LOADING,
  CLEAR_TODO_ERRORS,
  CLEAR_TODO_CURRENT,
  CLEAR_TODOS,
  UPDATE_STATUS,
} from "./types";

const config = {
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  },
};
//static messages for toast
const error_message = (message) => {
  return `<span class="error_messge_icon small"></span> <span>${message}</span>`;
};
const success_message = (message) => {
  return `<span class="success_messge_icon small"></span> <span>${message}</span>`;
};
//actions

//add todo item to server
export const addTodo = (id, item) => async (dispatch) => {
  console.log(id)
  await axios
    .post(`${process.env.REACT_APP_API_URL}/additem/${id}`, item, config)
    .then((res) => {
      console.log(res)
      dispatch({
        type: ADD_TODO,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//get todos from server
export const getTodos = (id) => async (dispatch) => {
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/todos/${id}`);
      const data = await res.json();
      dispatch({
        type: GET_TODOS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: TODO_ERRORS,
        payload: "Something went wrong",
      });
    }
  };

//delete todo from locastorage
export const deleteTodo = (id) => async (dispatch) => {
  await axios
    .delete(`${process.env.REACT_APP_API_URL}/todo/${id}`, config)
    .then((res) => {
      dispatch({
        type: DELETE_TODO,
        payload: id,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//update a todo item
export const updateTodo = (id, item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/updatetodo/${id}`, item, config)
    .then((res) => {
      dispatch({
        type: UPDATE_TODO,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: TODO_ERRORS,
        payload: err.response.data.errors,
      });
    });
};
//update a todo item
export const updateStatus= (id, status) => async (dispatch) => {
  let stat = { status: status }
  await axios
    .post(`${process.env.REACT_APP_API_URL}/updatestatus/${id}`, stat, config)
    .then((res) => {
      M.toast({ html: success_message(success) });
    })
    .catch((err) => {
      M.toast({ html: error_message(errors[Object.keys(errors)[0]]) });
    });
};

//set the current
export const setCurrent = (data)  => {
  return{
    type: GET_TODO,
    payload: data,
};
};

//clear todo errors
export const clearErrors = () => {
  return{
    type: CLEAR_TODO_ERRORS,
  } 
};

//clear stodos
export const clearTodos = () => {
  return {
    type: CLEAR_TODOS,
  };
};

//clear current
export const clearCurrent = () => {
  return {
    type: CLEAR_TODO_CURRENT,
  };
};

//set loading
export const setLoading = () => {
  console.log("loadingS");
  return {
    type: SET_TODO_LOADING,
  };
};
