import React, { useEffect } from "react";
import { connect } from "react-redux"
import { Link, useParams } from "react-router-dom";
import DataTable from "react-data-table-component";
import { addTodo, clearErrors, clearTodos, deleteTodo, getTodos, setCurrent } from "../../actions/TodoActions"
import { AddTodoItem } from "../modals/AddTodoItem";
import M from "materialize-css/dist/js/materialize.min.js";





const ListTodos = ({todo: {todos, loading, errors, success}, getTodos, addTodo, deleteTodo, setCurrent,clearTodos, clearErrors}) => {
const { id }= useParams()

const error_message = (message) =>{
  return `<span class="error_messge_icon small"></span> <span>${message}</span><a class="btn-flat toast-action modal-trigger" href="#add_todo_modal">Retry</a>`
}
const success_message = (message) =>{
  return `<span class="success_messge_icon small"></span> <span>${message}</span><a class="btn-flat toast-action modal-trigger" href="#add_todo_modal">Add New</a>`
}
  useEffect(() => {
    if(id){
      getTodos(id)
    }
    return () =>{
      clearTodos()
    }
  },[])

  useEffect(() => {
    //initialize materialize jsvsacript
    M.AutoInit();
  });

  useEffect(() => {
    if (errors !== null) {
      M.toast({html : error_message(errors[Object.keys(errors)[0]])})
    }else if(success !== null){
      M.toast({html : success_message(success)})
    }

  }, [errors, success])


  const changeStatus = (e) => {
      if (e.target.value === 'true') {
        //console.log(e.target.value);
      }else{
        //console.log(e.target.value);
      }
  };


  //delete Item
  const deleteItem = target_id =>{
      deleteTodo(target_id)
  }

  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };
  //columns of the table
  const columns = [
    {
      name: "title",
      selector: (row) => row["title"],
      sortable: true,
    },
    {
      name: "remark",
      selector: (row) => row["remark"],
      sortable: true,
    },
    {
      name: "Days left",
      //selector: (row) => (new Date(Math.abs(new Date() - new Date(row["date"]))) * 1000).getHours(),
      sortable: true,
    },
    {
      name: "completed",
      selector: (row) => row["completed"],
      cell: (row) =>
        row["completed"] === 1 ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
    },
    {
      name: "Switch completed",
      selector: (row) => row["completed"],
      cell: (row) => (
        <div className="switch">
          <label>
              <input
                type="checkbox"
                checked={!row.completed}
                onChange={changeStatus}
                value={row.completed}
              />
            <span className="lever"></span>
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <a href="#edit_todo_modal" className="modal-trigger" onClick={() =>setCurrent(row)}><i className="material-icons center cursor-pointer mx-4">edit</i></a>
          <a href="#confirmDelete" className="modal-trigger" onClick={() =>setCurrent(row)}><i className="material-icons center cursor-pointer deleteCan mx-4">delete_forever</i></a>
          <a className="waves-effect waves-light btn modal-trigger" href="#confirmDelete">Modal</a>
        </div>
      ),
      right: true,
    },
  ];


  return (
    <div style={{ marginBottom: "5%" }}>

      <AddTodoItem addTodo={addTodo} errors={errors} clearErrors={clearErrors} />



      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <div className="">
        {todos?.length > 0 && (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )}
      </div>
    </div>
  );
};

const mapStateToProps = state =>({
  todo: state.todo,
});

export default connect(mapStateToProps,{setCurrent, deleteTodo, getTodos, clearTodos, addTodo, clearErrors})(ListTodos);
