import React, { useState} from "react";
import { connect } from "react-redux"
import { addTodo, getTodos } from "../../actions/TodoActions"

export const AddTodoItem = ({addTodo, getTodos}) => {
  //state
  const [description, setDescription] = useState('');
  const [deadline, setDeadline] = useState('');

  const onDescriptionChange = (e) => {
    setDescription(e.target.value);
  };
  const onDeadlineChange = (e) => {
    setDeadline(e.target.value);
  };

  //submit form and persist to localstorage
  const onSubmit = (e) => {
    e.preventDefault();
    const data = {
      description,
      deadline
    }
    addTodo(data)
    getTodos()
  };

  return (
    <div id="add_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Add Todo</h6>
          <div className="row">
            <div className="input-field col s12">
              <input
                type="text"
                name="description"
                value={description}
                onChange={onDescriptionChange}
              />
              <label htmlFor="description">Description</label>
            </div>
            <div className="input-field col s12">
              <input
                type="date"
                name="deadline"
                value={deadline}
                onChange={onDeadlineChange}
              />
              <label htmlFor="deadline">Deadline</label>
              <span className="helper-text" data-error="wrong" data-success="right">Set the deadline for the todo. default is infinity</span>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="waves-effect waves-light btn">
            <i className="material-icons left">save</i>ADD
          </button>
        </div>
      </form>
    </div>
  );
};


export default connect(null, { addTodo , getTodos})(AddTodoItem);
