import React from "react";
import { Link } from "react-router-dom";
import DataTable from "react-data-table-component";

const ListTodos = () => {
  const changeStatus = (e) => {
      e.target.checked = false
    console.log(!e.target.value);
  };

  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };

  //columns of the table
  const columns = [
    {
      name: "description",
      selector: (row) => row["description"],
      sortable: true,
    },
    {
      name: "remark",
      selector: (row) => row["remark"],
      sortable: true,
    },
    {
      name: "Days left",
      selector: (row) => row["days"],
      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => row["status"],
      cell: (row) =>
        row["status"] === true ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => row["status"],
      cell: (row) => (
        <div class="switch">
          <label>
            Off
              <input
                type="checkbox"
                checked={row.status}
                onChange={changeStatus}
                value={row.status}
              />
            <span class="lever"></span>
            On
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <i className="material-icons center">done</i>
          <i className="material-icons center">edit</i>
          <i className="material-icons center">delete_forever</i>
        </div>
      ),
      right: true,
    },
  ];

  const todos = [
    {
      description: "Hello world",
      status: true,
      remark: "dompleted",
    },
    {
      description: "pello world",
      status: false,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: false,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
  ];

  return (
    <div style={{ marginBottom: "5%" }}>
      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <div className="">
        {todos?.length > 0 && (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )}
      </div>
    </div>
  );
};

export default ListTodos;
