import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Link, useParams } from "react-router-dom";
import DataTable from "react-data-table-component";
import AddTodoItem from "../modals/AddTodoItem";
import {addTodo, clearErrors, clearTodos, deleteTodo, getTodos, setCurrent,updateStatus
} from "../../actions/TodoActions";
import { getTodolists } from "../../actions/TodolistActions"
import M from "materialize-css/dist/js/materialize.min.js";
import EditTodoItem from "../modals/EditTodoItem";
import TodoItemDetails from "../modals/TodoItemDetails";
import Loader from "../inc/Loader";

const ListTodos = ({owner:{user, login}, todo: { todos, loading, errors, success }, mytodolists:{todolists},getTodos,
  addTodo, deleteTodo, setCurrent, clearTodos,clearErrors,getTodolists,updateStatus}) => {

  const { id } = useParams();

  const error_message = (message) => {
    return `<span class="error_messge_icon small"></span> <span>${message}</span><a class="btn-flat toast-action modal-trigger" href="#add_todo_modal">Retry</a>`;
  };
  const success_message = (message) => {
    return `<span class="success_messge_icon small"></span> <span>${message}</span><a class="btn-flat toast-action modal-trigger" href="#add_todo_modal">Add New</a>`;
  };
  useEffect(() => {

    if (id) {
      getTodos(id);
    }
    return () => {
      clearTodos();
    };
    // eslint-disable-next-line
  }, [id]);


useEffect(() =>{
  if(user){
    getTodolists(user.id)
  }

    // eslint-disable-next-line
},[user])


  useEffect(() => {
    if (errors !== null) {
      M.toast({ html: error_message(errors[Object.keys(errors)[0]]) });
    } else if (success !== null) {
      M.toast({ html: success_message(success) });
    }
  }, [errors, success]);

  const changeStatus = async (t_id, status) => {
      await updateStatus(t_id, status)
      await getTodos(id)
  };


  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };
  //columns of the table
  const columns = [
    {
      name: "title",
      selector: (row) => row["title"],
      cell : row => <a href="#todo_details_modal" className="modal-trigger"
      onClick={() => setCurrent(row)}>
      {row["title"]}
    </a>,
      sortable: true,
      grow: 4,
    },
    {
      name: "completed",
      selector: (row) => row["completed"],
      cell: (row) =>
        row["completed"] === 1 ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
      center: true,
    },
    {
      name: "Switch",
      selector: (row) => row["completed"],
      cell: (row) => (
        <div className="switch">
          <label>
            <input
              type="checkbox"
              checked={!row.completed}
              onChange={() => changeStatus(row.id, row.completed)}
              value={row.completed}
            />
            <span className="lever"></span>
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <a
            href="#edit_todo_modal"
            className="modal-trigger"
            onClick={() => setCurrent(row)}
          >
            <i className="material-icons center cursor-pointer mx-4">edit</i>
          </a>
          <a
            href="#confirmDelete"
            className="modal-trigger"
            onClick={() => setCurrent(row)}
          >
            <i className="material-icons center cursor-pointer deleteCan mx-4">
              delete_forever
            </i>
          </a>
        </div>
      ),
      right: true,
    },
  ];

  return (
    <div style={{ marginBottom: "5%" }}>
      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <AddTodoItem /> 
      <EditTodoItem  /> 
      <TodoItemDetails  /> 
      <div className="">
        {
        !loading  ? todos?.length > 0 ? (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )
        :
        <div className="text-center" style={{marginTop:'25%'}}>No Todos in this Todolist Folder Yet</div>
        :
        <Loader />
        }
      </div>
    </div>
  );
};

const mapStateToProps = (state) => ({
  todo: state.todo,
  mytodolists: state.todolist,
  owner: state.user,
});

export default connect(mapStateToProps, {
  setCurrent,
  deleteTodo,
  getTodos,
  clearTodos,
  addTodo,
  updateStatus,
  clearErrors,
  getTodolists,
})(ListTodos);
