import React from 'react'
import { Link } from 'react-router-dom';

const ListTodos = () => {


    //style head of table
    const customStyles = {
        headCells: {
          style: {
            paddingLeft: '8px', // override the cell padding for head cells
            paddingRight: '8px',
            backgroundColor: "rgba(37, 99, 235)",
            fontSize: "1rem",
            fontWeight: "400",
          }
      }
    }


    //columns of the table
    const columns = [
        {
            name: "description",
            selector: row => row["description"],
            sortable: true
        },
        {
            name: "remark",
            selector: row => row["remark"],
            sortable: true
        },
        {
            name: "days",
            selector: row => row["days"],
            sortable: true
        },
        {
          name: "",
          cell: row => (
              <div className="flex">
                  <i class="material-icons center">delete</i>
                  <i class="material-icons center">delete</i>
                  <i class="material-icons center">delete</i>
              </div>
          ),
          right: true
      },
    ];

    const todos = [
        {
            description: 'Hello world',
            status: true,
            remark: 'Completed'
        },
        {
            description: 'Hello world',
            status: true,
            remark: 'Completed'
        },
        {
            description: 'Hello world',
            status: true,
            remark: 'Completed'
        },
        {
            description: 'Hello world',
            status: true,
            remark: 'Completed'
        },
    ]




    return (
        <>
            <div className="">
                <Link to="/" className="blue">Home</Link>
            </div>
            <div className="">
                {todos?.length > 0 && (
                    <DataTable
                        title="Brands"
                        columns={columns}
                        data={todos}
                        customStyles={customStyles}
                        pagination={true}
                    />
                )}

            </div>
        </>
    );
}

export default ListTodos
