import axios from "axios";

import {
  ADD_TODO_LIST,
  DELETE_TODO_LIST,
  ERRORS,
  UPDATE_TODO_LIST,
  GET_TODO_LIST,
  GET_TODO_LISTS,
  SET_LOADING,
  CLEAR_ERRORS,
  CLEAR_CURRENT,
} from "./types";

const config = {
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
  },
};
//actions

//add todo item to server
export const addTodolist = (item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/addtodolist`, item, config)
    .then((res) => {
      dispatch({
        type: ADD_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      console.log(err.response)
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//get todos from server
export const getTodolists =() => async (dispatch) => {
  setLoading()
    try {
      const res = await fetch(`${process.env.REACT_APP_API_URL}/todolists`);
      const data = await res.json();
      dispatch({
        type: GET_TODO_LISTS,
        payload: data,
      });
    } catch (error) {
      dispatch({
        type: ERRORS,
        payload: "Something went wrong",
      });
    }
  };

//delete todo from locastorage
export const deleteTodolist = (id) => async (dispatch) => {
  await axios
    .delete(`${process.env.REACT_APP_API_URL}/todolist/${id}`, config)
    .then((res) => {
      dispatch({
        type: DELETE_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//update a todo item
export const updateTodolist = (id, item) => async (dispatch) => {
  await axios
    .post(`${process.env.REACT_APP_API_URL}/updatetodolist/${id}`, item, config)
    .then((res) => {
      dispatch({
        type: UPDATE_TODO_LIST,
        payload: res.data,
      });
    })
    .catch((err) => {
      dispatch({
        type: ERRORS,
        payload: err.response.data.errors,
      });
    });
};

//set the current
export const setCurrent = (data) => (dispatch) => {
  dispatch({
    type: GET_TODO_LIST,
    payload: data,
  });
};

//clear todo errors
/* export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS
  };
}; */

//clear current
export const clearCurrent = () => {
  return {
    type: CLEAR_CURRENT,
  };
};

//set loading
export const setLoading = () => {
  return {
    type: SET_LOADING,
  };
};
