import React, { useEffect} from 'react'
import { Link } from "react-router-dom";
import { connect } from "react-redux"
import { getTodolists } from "../../actions/TodolistActions"


const HomePage = ({todolists,loading, getTodolists}) => {
    React.useEffect(() =>{
        getTodolists()
    },[])
    return (
        <div className="homepage">

            <p className="text-grey"> 
                In order to be able to keep track of your todos, You need to
                <Link to="/register">Create an account</Link> with us. <br />
                You already have an <Link to="/register">account</Link> You can please <Link to="/login">login</Link>
            </p>
            <p className="text-grey"> 
                You have the following list of TodoList:
            </p>
            <ul className="homepagelists">
                {
                    !loading ? (
                        todolists?.length > 0 ? (
                            todolists.map(t =>{
                               return <li><i className="material-icons center" style={{lineHeight:'0', fontSize:'inherit'}}>done</i> <Link to={`todos`} class="collection-item"><span class="badge">{t.todos.length}</span>{t.name}</Link></li> 
                            })
                        ):('Empty')
                    ):("loading")
                }
            </ul>
            <p className="text-grey">
                You have not yet create a todolist

            </p>
            <button className="waves-effect modal-trigger waves-light btn-small" href="#create_todolist_modal"><i className="material-icons left">create_new_folder</i>Create New</button>

        </div>
    )
}


const mapStateToProps = state =>({
    todolists: state.todolist.todolists,
    loading: state.todolist.loading,
})

export default connect(mapStateToProps,{getTodolists}) (HomePage);
