import React, { useEffect } from "react";
import { connect } from "react-redux"
import { Link, useParams } from "react-router-dom";
import DataTable from "react-data-table-component";
import { clearTodos, deleteTodo, getTodos, setCurrent } from "../../actions/TodoActions"
import { AddTodoItem } from "../modals/AddTodoItem";



const ListTodos = ({todo: {todos, loading}, getTodos, deleteTodo, setCurrent,clearTodos}) => {
const { id }= useParams()
  useEffect(() => {
    if(id){
      getTodos(id)
    }
    return () =>{
      clearTodos()
    }
  },[])


  const changeStatus = (e) => {
      if (e.target.value === 'true') {
        //console.log(e.target.value);
      }else{
        //console.log(e.target.value);
      }
  };


  //delete Item
  const deleteItem = id =>{
      deleteTodo(id)
      getTodos();
  }

  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };
  //columns of the table
  const columns = [
    {
      name: "title",
      selector: (row) => row["title"],
      sortable: true,
    },
    {
      name: "remark",
      selector: (row) => row["remark"],
      sortable: true,
    },
    {
      name: "Days left",
      //selector: (row) => (new Date(Math.abs(new Date() - new Date(row["date"]))) * 1000).getHours(),
      sortable: true,
    },
    {
      name: "completed",
      selector: (row) => row["completed"],
      cell: (row) =>
        row["completed"] === 1 ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
    },
    {
      name: "Switch completed",
      selector: (row) => row["completed"],
      cell: (row) => (
        <div className="switch">
          <label>
              <input
                type="checkbox"
                checked={!row.completed}
                onChange={changeStatus}
                value={row.completed}
              />
            <span className="lever"></span>
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <a  href="#edit_todo_modal" className="modal-trigger" onClick={() =>setCurrent(row)}><i className="material-icons center cursor-pointer mx-4">edit</i></a>
          <i className="material-icons center cursor-pointer deleteCan mx-4" onClick={() => deleteItem(row.id)}>delete_forever</i>
        </div>
      ),
      right: true,
    },
  ];


  return (
    <div style={{ marginBottom: "5%" }}>

      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <div className="">
        {todos?.length > 0 && (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )}
      </div>
    </div>
  );
};

const mapStateToProps = state =>({
  todo: state.todo,
});

export default connect(mapStateToProps,{setCurrent, deleteTodo, getTodos, clearTodos})(ListTodos);
