import axios from "axios";
import M from "materialize-css/dist/js/materialize.min.js";

import {REGISTER_USER, LOGIN_USER, USER_ERROR} from "./types"

//configuration
const config ={
    headers:{
        'Content-type' :'application/json'
    }
}

//check if there is a user object in localstorage 
//if there is, turn login in state to true




//actions
//CREATE AN ACCOUNT
export const register = (data) => async (dispatch) =>{
    await axios.post(`${process.env.REACT_APP_API_URL}/register/owner`, data, config)
    .then(res =>{
        dispatch({
            type: REGISTER_USER,
            payload: res.data
        })
        //update local storage with res.data
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}


//login user
export const login = (data) =>async (dispatch) =>{
    await axios.get(`${process.env.REACT_APP_API_URL}/register/owner`, data, config)
    .then(res =>{
        dispatch({
            type: LOGIN_USER,
            payload: res.data
        })
        //update local storage with res.data
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}
