import React, { useEffect} from "react";
import { connect } from "react-redux"
import { deleteTodo, clearCurrent } from "../../actions/TodoActions";

const ConfirmDelete = ({current, deleteTodo, clearCurrent}) => {

    /* useEffect(() => {
        return () => {
            clearCurrent()
        }
    }, []) */



  return (
    <div id="confirmDelete" className="modal">
      <div className="modal-content">
        <h4 className="text-center"><i className="large material-icon">delete</i></h4>
        <p>You are about to delete <span className="bold">{current && current.title}</span></p>
        <p className="my-4">Are you sure you want to delete?</p>
      </div>
      <div className="modal-footer" style={{display:'flex'}}>
        <a href="#!" className="modal-close waves-effect waves-green btn-flat">
          No
        </a>
        <button onClick={() => deleteTodo(current && current.id)} href="#!" className="px-2 py-1 red modal-close waves-effect btn-flat">
          Yes
        </button>
      </div>
    </div>
  );
};

const mapStateToProps = state => ({
    current: state.todo.current,
  })

export default connect(mapStateToProps, {deleteTodo, clearCurrent})(ConfirmDelete);
