import {
  ADD_TODO_LIST,
  DELETE_TODO_LIST,
  TODO_LIST_ERRORS,
  UPDATE_TODO_LIST,
  GET_TODO_LISTS,
  GET_TODO_LIST,
  SET_LOADING,
  CLEAR_ERRORS,
} from "../actions/types";

const initialState = {
  todolists: null,
  current: null,
  loading: true,
  errors: null,
};




export default (state = initialState, action) => {
  switch (action.type) {
    case GET_TODO_LISTS:
      return{
        ...state,
        todolists: action.payload,
        loading: false
      }
    case ADD_TODO_LIST:
    case UPDATE_TODO_LIST:
      return{
        ...state,
        todolists: [...state.todolists, action.payload.data],
        loading: false
      }

    case GET_TODO_LIST:
      return{
        ...state,
        current: action.payload
      }

    case TODO_LIST_ERRORS:
      return{
        ...state,
        errors:action.payload
      }
    case SET_LOADING:
      return {
        ...state,
        loading: true,
      };
    /* case CLEAR_ERRORS:
      return {
        ...state,
        errors: null,
      }; */

    default:
      return state;
  }
};
