import {ADD_TODO,DELETE_TODO,ERRORS,UPDATE_TODO,GET_TODOS,GET_TODO,SET_LOADING,CLEAR_ERRORS} from '../actions/types'

const initialState = {
  items: null,
  item: null,
  loading: false,
  error: null,
};

export default (state = initialState, action) => {
  switch (action.type) {
    case SET_LOADING:
      return{
        ...state,
        loading: true
      }
    case CLEAR_ERRORS:
      return{
        ...state,
        errors: null
      }




    default:
      return state;
  }
};
