import {
  ADD_TODO,
  DELETE_TODO,
  ERRORS,
  UPDATE_TODO,
  GET_TODOS,
  GET_TODO,
  SET_LOADING,
  CLEAR_ERRORS,
} from "../actions/types";

const initialState = {
  todos: null,
  current: null,
  loading: false,
  errors: null,
};




export default (state = initialState, action) => {
  switch (action.type) {
    case GET_TODOS:
      return{
        ...state,
        todos: action.payload,
        loading: false
      }
    case ADD_TODO:
    case UPDATE_TODO:
      return{
        ...state,
        todos: [...state.todos, action.payload.data],
        loading: false
      }

    case GET_TODO:
      return{
        ...state,
        current: action.payload
      }

    case ERRORS:
      return{
        ...state,
        errors:action.payload
      }
    case SET_LOADING:
      return {
        ...state,
        loading: true,
      };
    case CLEAR_ERRORS:
      return {
        ...state,
        errors: null,
      };

    default:
      return state;
  }
};
