import React, { useState, useEffect } from "react";
import { connect } from "react-redux";
import { useParams, withRouter} from "react-router-dom";
import { addTodo, clearErrors} from "../../actions/TodoActions";

export const AddTodoItem = ({ errors, success, addTodo, clearErrors }) => {

  const { id } = useParams()

  //state
  const [data, setData] = useState({title:'', deadline:''})
  const { title, deadline } = data;


  useEffect(() => {
    return () =>{
      if(errors){
        clearErrors();
      }
    }
  }, [errors]);

  const onChange = (e) => {
    setData({...data, [e.target.name]: e.target.value})
    if(errors){
      clearErrors();
    }

  };


  //submit form and persist to localstorage
  const onSubmit = (e) => {
    e.preventDefault();
    addTodo(id , data);
  };

  return (
    <div id="add_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Add Todo</h6>
          <div className="row">
            <div className="input-field col s12">
              <input
                type="text"
                name="title"
                value={title}
                onChange={onChange}
              />
              <label htmlFor="title">Title</label>
              {errors?.title && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.title[0]}
                </span>
              )}
            </div>
            <div className="input-field col s12">
              <input
                type="date"
                name="deadline"
                value={deadline}
                onChange={onChange}
              />
              <label htmlFor="deadline">Deadline</label>
              {errors?.deadline && (
                <span className="helper-text" style={{ color: "red" }}>
                  {errors.deadline[0]}
                </span>
              )}
            </div>

          </div>
        </div>
        <div className="modal-footer">
          <button
            type="submit"
            className={`modal-close waves-effect waves-light btn`}
            //disabled={errors !== null}
          >
            <i className="material-icons left">save</i>ADD
          </button>
        </div>
      </form>
    </div>
  );
};

const mapStateToProps = (state) => ({
  errors: state.todo.errors,
  success: state.todo.success,
});

export default withRouter(connect(mapStateToProps, { addTodo, clearErrors })(AddTodoItem));
