import React, { useState } from "react";

const EditTodoList = () => {

    //state
    const [todolistName, setTodolistName] = useState('HELLO')


    //ON INPUT CHANGES
    const onChange = (e) =>{
        setTodolistName(e.target.value)
    }

    //submit the form
    const onSubmit = (e) =>{
        e.preventDefault()
        alert('hello world')
    }


  return (
    <div id="edit_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Edit New Todo List</h6>
          <div className="row">
            <div className="input-field col s12">
              <input type="text" name="todolistName" value={todolistName} onChange={onChange} />
              <label htmlFor="name">Name</label>
            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="waves-effect waves-light btn">
            <i className="material-icons left">update</i>UPDATE
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditTodoList;
