import React from "react";
import { Link } from "react-router-dom";
import DataTable from "react-data-table-component";

const ListTodos = () => {
  const changeStatus = (e) => {
      if (e.target.value === 'true') {
        console.log(e.target.value);
      }else{
        console.log(e.target.value);
      }
  };


  //delete Item
  const deleteItem = id =>{
      alert(id)
  }

  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };
console.log(new Date())
  //columns of the table
  const columns = [
    {
      name: "description",
      selector: (row) => row["description"],
      sortable: true,
    },
    {
      name: "remark",
      selector: (row) => row["remark"],
      sortable: true,
    },
    {
      name: "Days left",
      selector: (row) => (new Date(Math.abs(new Date() - new Date(row["date"]))) * 1000).getHours(),
      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => row["status"],
      cell: (row) =>
        row["status"] === true ? (
          <i className="material-icons center">done</i>
        ) : (
          <i className="material-icons center">do_not_disturb</i>
        ),

      sortable: true,
    },
    {
      name: "Status",
      selector: (row) => row["status"],
      cell: (row) => (
        <div class="switch">
          <label>
              <input
                type="checkbox"
                checked={!row.status}
                onChange={changeStatus}
                value={row.status}
              />
            <span class="lever"></span>
          </label>
        </div>
      ),

      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <a  href="#edit_todo_modal" className="modal-trigger"><i className="material-icons center cursor-pointer mx-4">edit</i></a>
          <i className="material-icons center cursor-pointer deleteCan mx-4" onClick={() => deleteItem(row.description)}>delete_forever</i>
        </div>
      ),
      right: true,
    },
  ];

  const todos = [
    {
      description: "Hello world",
      status: true,
      remark: "dompleted",
      date: "2011-02-07 15:13:06",
    },
    {
      description: "pello world",
      status: false,
      remark: "Completed",
      date: "2021-02-07 15:13:06",
    },

  ];

  return (
    <div style={{ marginBottom: "5%" }}>
      <div className="fixed-action-btn" style={{ top: "0", marginTop: "6%" }}>
        <a
          className="btn-floating btn-large green modal-trigger"
          href="#add_todo_modal"
        >
          <i className="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <div className="">
        {todos?.length > 0 && (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )}
      </div>
    </div>
  );
};

export default ListTodos;
