import axios from "axios";
import M from "materialize-css/dist/js/materialize.min.js";
import {isEmptyObject} from "../components/inc/checkIsEmpty"

import {REGISTER_USER, LOGIN_USER, USER_ERROR, CLEAR_USER_ERRORS} from "./types"

//configuration
const config ={
    headers:{
        'Content-type' :'application/json'
    }
}


//toasts
const success_message = (message) => {
    return `<span class="success_messge_icon small"></span> <span>${message}. You will be redirected</span>`;
  };



//actions
//CREATE AN ACCOUNT
export const register = (data) => async (dispatch) =>{
    await axios.post(`${process.env.REACT_APP_API_URL}/register/user`, data, config)
    .then(res =>{
        dispatch({
            type: REGISTER_USER,
            payload: res.data
        })
        M.toast({ html: success_message(res.data.message) });
        //update local storage with res.data
        localStorage.setItem("todo_user", JSON.stringify(res.data.data))
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}


//login user
export const login = (data) =>async (dispatch) =>{
    await axios.get(`${process.env.REACT_APP_API_URL}/login/user`, data, config)
    .then(res =>{
        dispatch({
            type: LOGIN_USER,
            payload: res.data
        })
        //update local storage with res.data
    }).catch(err =>{
        dispatch({
            type: USER_ERROR,
            payload: err.response.data.errors
        })
    })
}


//clear errors
export const clearErrors = () =>{
    return{
        type: CLEAR_USER_ERRORS
    }
}
