import React from "react";
import { Link } from "react-router-dom";
import DataTable from "react-data-table-component";

const ListTodos = () => {
  //style head of table
  const customStyles = {
    headCells: {
      style: {
        paddingLeft: "8px", // override the cell padding for head cells
        paddingRight: "8px",
        backgroundColor: "rgba(37, 99, 235)",
        fontSize: "1rem",
        fontWeight: "400",
      },
    },
  };

  //columns of the table
  const columns = [
    {
      name: "description",
      selector: (row) => row["description"],
      sortable: true,
    },
    {
      name: "remark",
      selector: (row) => row["remark"],
      sortable: true,
    },
    {
      name: "days",
      selector: (row) => row["days"],
      sortable: true,
    },
    {
      name: "",
      cell: (row) => (
        <div className="flex center">
          <i class="material-icons center">done</i>
          <i class="material-icons center">edit</i>
          <i class="material-icons center">delete_forever</i>
        </div>
      ),
      right: true,
    },
  ];

  const todos = [
    {
      description: "Hello world",
      status: true,
      remark: "dompleted",
    },
    {
      description: "pello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
    {
      description: "Hello world",
      status: true,
      remark: "Completed",
    },
  ];

  return (
    <>
      <div class="fixed-action-btn" style={{marginBottom:"25%"}}>
        <a class="btn-floating btn-large blue modal-trigger" href="#add_todo_modal">
          <i class="small material-icons">add</i>
        </a>
      </div>
      <div className="">
        <Link to="/" className="grelen" style={{ fontSize: "22px" }}>
          Home
        </Link>
      </div>
      <div className="">
        {todos?.length > 0 && (
          <DataTable
            title="Todos"
            columns={columns}
            data={todos}
            customStyles={customStyles}
            pagination={true}
          />
        )}
      </div>
    </>
  );
};

export default ListTodos;
