import React, { useState, useEffect } from "react";
import {connect } from "react-redux"
import { clearErrors,} from "../../actions/TodoActions"


const TodoItemDetails = ({current}) => {

  return (
    <div id="todo_details_modal" className="modal">
      {
        current ? <div className="row">
          <div class="modal-content todo-details">
          <div className="cols12"><span>Title: </span><span>{current.title}</span></div>
          <div className="cols12"><span>Status: </span><span>{current.completed === true ? 'Completed' : "Not yet Completed"}</span></div>
          <div className="cols12"><span>Deadline: </span><span>{current.deadline ? current.deadline : 'Today'}</span></div>
          <div className="cols12"><span>Todolist: </span><span>{current.todolist.name}</span></div>
          <div className="cols12"><span>Remark: </span><span>{current.title}</span></div>
        </div>
        <div class="modal-footer">
          <a href="#edit_todo_modal" class="modal-close modal-trigger waves-effect waves-green btn-flat">Edit</a>
        </div>
        </div>

        : <div className="text-red">Something went wrong</div>
      }
    </div>
  );
};


const mapStateToProps = state => ({
  current: state.todo.current,
})

export default connect(mapStateToProps)(TodoItemDetails);
