import React, { useState, useEffect } from "react";
import {connect } from "react-redux"
import { clearErrors, current,  getTodos, updateTodo } from "../../actions/TodoActions"


const EditTodoList = ({current,errors, updateTodo, getTodos, clearErrors}) => {

    //state
    const [data, setData] = useState({
      title : '',
      deadline: ''
    })
    const { title, deadline } = data;

    useEffect(() => {
      if (current) {
        setData({
          title: current.title,
          deadline: current.deadline
        })
      }
    }, [current])



    //ON INPUT CHANGES
    const onChange = (e) =>{
        setData({...data, [e.target.name] : e.target.value})
        if(errors){
          clearErrors()
        }
    }

    //submit the form
    const onSubmit = (e) =>{
        e.preventDefault()
        updateTodo(current.id, data)
    }


  return (
    <div id="edit_todo_modal" className="modal">
      <form onSubmit={onSubmit}>
        <div className="modal-content">
          <h6>Edit New Todo List</h6>
          <div className="row">
            <div className="input-field col s12">
              <input type="text" name="title" value={title ? title : ''} onChange={onChange} />

            </div>
          </div>
          <div className="row">
            <div className="input-field col s12">
              <input type="date" name="deadline" value={deadline ? deadline : ''} onChange={onChange} />

            </div>
          </div>
        </div>
        <div className="modal-footer">
          <button type="submit" className="modal-close waves-effect waves-light btn">
            <i className="material-icons left">update</i>UPDATE
          </button>
        </div>
      </form>
    </div>
  );
};


const mapStateToProps = state => ({
  current: state.todo.current,
  errors: state.todo.errors,
})

export default connect(mapStateToProps, {updateTodo, getTodos, clearErrors})(EditTodoList);
